// Parse AI Response and Prepare Actions
const aiResponse = JSON.parse($input.first().json.output);

// Validate the response structure
if (!aiResponse || !aiResponse.irrigation || !aiResponse.mowing) {
  throw new Error('Invalid AI response structure');
}

// Prepare autonomous actions
const actions = [];
const currentHour = new Date().getHours();

// Process irrigation recommendations
if (aiResponse.irrigation.recommendation === 'irrigate' && aiResponse.irrigation.confidence >= 70) {
  for (const zone of aiResponse.irrigation.zones_to_water) {
    // Only irrigate during optimal hours (6-8 AM or 6-8 PM)
    if ((currentHour >= 6 && currentHour <= 8) || (currentHour >= 18 && currentHour <= 20)) {
      for (const rainbirdZone of zone.rainbird_zones) {
        actions.push({
          type: 'irrigate',
          zone: rainbirdZone,
          duration: zone.duration,
          priority: zone.priority,
          reason: zone.reason,
          execute_immediately: true,
          delay_minutes: 0
        });
      }
    } else {
      // Schedule for next optimal time
      let nextOptimalTime = 6; // Default to 6 AM next day
      if (currentHour < 18) {
        nextOptimalTime = 18; // 6 PM today if before 6 PM
      }

      const delayMinutes = (nextOptimalTime - currentHour) * 60;

      for (const rainbirdZone of zone.rainbird_zones) {
        actions.push({
          type: 'irrigate',
          zone: rainbirdZone,
          duration: zone.duration,
          priority: zone.priority,
          reason: zone.reason,
          execute_immediately: false,
          delay_minutes: delayMinutes > 0 ? delayMinutes : (24 + nextOptimalTime - currentHour) * 60
        });
      }
    }
  }
}

// Add logging action
actions.push({
  type: 'log_analysis',
  data: aiResponse,
  execute_immediately: true,
  delay_minutes: 0
});

return {
  ai_response: aiResponse,
  autonomous_actions: actions,
  should_execute_irrigation: actions.some(a => a.type === 'irrigate' && a.execute_immediately),
  total_zones_to_water: actions.filter(a => a.type === 'irrigate').length,
  timestamp: new Date().toISOString(),
  analysis_summary: {
    irrigation_confidence: aiResponse.irrigation.confidence,
    mowing_confidence: aiResponse.mowing.confidence,
    season: aiResponse.location.season,
    recommended_actions: actions.length
  }
};

